import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const TestContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: ${props => props.color};
  transition: background-color 0.3s ease;
`;

const ExitButton = styled.button`
  position: fixed;
  top: 1.5rem;
  left: 1.5rem;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  border: none;
  border-radius: 0.5rem;
  padding: 0.75rem 1.25rem;
  font-size: 1.1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.2s ease;
  z-index: 100;

  &:hover {
    background: rgba(0, 0, 0, 0.9);
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }

  svg {
    width: 20px;
    height: 20px;
  }
`;

const FullScreenButton = styled(ExitButton)`
  left: auto;
  right: 1.5rem;
`;

const ControlPanel = styled.div`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  background: white;
  border-radius: 0.75rem;
  box-shadow: 0 5px 30px rgba(0, 0, 0, 0.15);
  width: 400px;
  padding: ${props => props.isMinimized ? '1.25rem' : '2rem'};
  color: #333;
  transition: all 0.3s ease;
  transform: translateY(${props => props.isMinimized ? 'calc(100% - 4rem)' : '0'});
  backdrop-filter: blur(10px);
  max-height: calc(100vh - 4rem);
  overflow-y: auto;
`;

const PanelHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: ${props => props.isMinimized ? '0' : '1.5rem'};
  padding-bottom: ${props => props.isMinimized ? '0' : '1rem'};
  border-bottom: ${props => props.isMinimized ? 'none' : '1px solid rgba(0, 0, 0, 0.1)'};

  h2 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 600;
    color: #333;
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
`;

const MinimizeButton = styled.button`
  background: #4169e1;
  border: none;
  color: white;
  font-size: 1.75rem;
  cursor: pointer;
  width: 2.5rem;
  height: 2.5rem;
  line-height: 1;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
  padding: 0;

  &:hover {
    background: #3658c5;
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const Description = styled.p`
  color: #666;
  line-height: 1.6;
  margin-bottom: 2rem;
`;

const Section = styled.div`
  margin-bottom: 2rem;

  h3 {
    font-size: 0.9rem;
    color: #666;
    margin-bottom: 1rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.columns}, 1fr);
  gap: ${props => props.showGridLines ? '1px' : '0'};
  background: ${props => props.showGridLines ? '#333' : 'transparent'};
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  transition: all ${props => props.transitionSpeed}ms ease;
`;

const Cell = styled.div`
  background: ${props => props.color};
  width: 100%;
  height: 100%;
  transition: background-color 0.3s ease;
`;

const ColorPreview = styled.div`
  width: 100%;
  height: 40px;
  background: ${props => props.color};
  border-radius: 0.5rem;
  margin-bottom: 1rem;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const RangeControl = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-bottom: 1rem;

  label {
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: #666;
  }

  input[type="range"] {
    width: 100%;
  }
`;

const ToggleButton = styled.button`
  background: ${props => props.active ? '#4169e1' : '#f0f0f0'};
  color: ${props => props.active ? 'white' : '#666'};
  border: none;
  border-radius: 0.5rem;
  padding: 0.75rem 1rem;
  cursor: pointer;
  transition: all 0.2s ease;
  width: 100%;

  &:hover {
    background: ${props => props.active ? '#3658c5' : '#e0e0e0'};
  }
`;

const ResetButton = styled.button`
  width: 100%;
  padding: 1rem;
  background: #4169e1;
  color: white;
  border: none;
  border-radius: 0.5rem;
  cursor: pointer;
  font-weight: 500;
  font-size: 1.1rem;
  margin-top: 1rem;

  &:hover {
    background: #3658c5;
  }
`;

const Checkerboard = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: ${props => `
    linear-gradient(45deg, ${props.color1} 25%, transparent 25%),
    linear-gradient(-45deg, ${props.color1} 25%, transparent 25%),
    linear-gradient(45deg, transparent 75%, ${props.color1} 75%),
    linear-gradient(-45deg, transparent 75%, ${props.color1} 75%)
  `};
  background-size: ${props => `${props.size}px ${props.size}px`};
  background-position: 0 0, 0 ${props => props.size/2}px, ${props => props.size/2}px ${props => -props.size/2}px, ${props => -props.size/2}px 0px;
  background-color: ${props => props.color2};
`;

const Crosshair = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 40px;
  height: 40px;
  pointer-events: none;

  /* Vertical line */
  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    width: 4px;
    height: 100%;
    transform: translateX(-50%);
    background: yellow;
    border: 1px solid black;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  }

  /* Horizontal line */
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 4px;
    transform: translateY(-50%);
    background: yellow;
    border: 1px solid black;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  }

  /* Gap in the center */
  &::before,
  &::after {
    background: linear-gradient(
      to bottom,
      yellow 0%,
      yellow 42%,
      transparent 42%,
      transparent 58%,
      yellow 58%,
      yellow 100%
    );
  }
`;

const TechnicalInfo = styled.div`
  background: #f8f9fa;
  border-radius: 0.5rem;
  padding: 1rem;
  margin-top: 1rem;
  font-size: 1rem;
  color: #666;

  div {
    display: flex;
    justify-content: space-between;
    margin-bottom: 0.5rem;

    &:last-child {
      margin-bottom: 0;
    }
  }

  span:last-child {
    font-weight: 500;
    color: #333;
  }
`;

const CollapsibleHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  cursor: pointer;
  padding: 0.5rem 0;
  user-select: none;

  &:hover h3 {
    color: #4169e1;
  }

  svg {
    width: 20px;
    height: 20px;
    color: #666;
    transition: transform 0.2s ease;
    transform: rotate(${props => props.isOpen ? '180deg' : '0deg'});
  }

  &:hover svg {
    color: #4169e1;
  }
`;

const CollapsibleContent = styled.div`
  overflow: hidden;
  max-height: ${props => props.isOpen ? '1000px' : '0'};
  opacity: ${props => props.isOpen ? '1' : '0'};
  transition: all 0.3s ease-in-out;
  margin-top: ${props => props.isOpen ? '1rem' : '0'};
`;

const UniformityTest = () => {
  const [brightness, setBrightness] = useState(50);
  const [isMinimized, setIsMinimized] = useState(false);
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [showGridLines, setShowGridLines] = useState(false);
  const [gridSize, setGridSize] = useState(3);
  const [selectedColor, setSelectedColor] = useState('white');
  const [patternType, setPatternType] = useState('solid'); // solid, checkerboard
  const [showCrosshair, setShowCrosshair] = useState(false);
  const [transitionSpeed, setTransitionSpeed] = useState(300);
  const [checkerboardSize, setCheckerboardSize] = useState(50);
  const [isDisplayInfoOpen, setIsDisplayInfoOpen] = useState(false);

  const toggleFullScreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen().catch((err) => {
        console.log(`Error attempting to enable fullscreen: ${err.message}`);
      });
      setIsFullScreen(true);
    } else {
      document.exitFullscreen();
      setIsFullScreen(false);
    }
  };

  useEffect(() => {
    const handleFullScreenChange = () => {
      setIsFullScreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullScreenChange);
    return () => {
      document.removeEventListener('fullscreenchange', handleFullScreenChange);
    };
  }, []);

  const getBackgroundColor = () => {
    if (selectedColor === 'white') {
      return `rgb(${brightness * 2.55}, ${brightness * 2.55}, ${brightness * 2.55})`;
    }
    const colors = {
      red: `rgb(${brightness * 2.55}, 0, 0)`,
      green: `rgb(0, ${brightness * 2.55}, 0)`,
      blue: `rgb(0, 0, ${brightness * 2.55})`,
      black: 'rgb(0, 0, 0)',
    };
    return colors[selectedColor] || colors.white;
  };

  const handleReset = () => {
    setBrightness(50);
    setGridSize(3);
    setShowGridLines(false);
    setSelectedColor('white');
    setPatternType('solid');
    setShowCrosshair(false);
    setTransitionSpeed(300);
    setCheckerboardSize(50);
  };

  return (
    <TestContainer color={getBackgroundColor()}>
      <ExitButton onClick={() => window.history.back()}>
        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="M15 19l-7-7 7-7" />
        </svg>
        Exit Test
      </ExitButton>

      <FullScreenButton onClick={toggleFullScreen}>
        {isFullScreen ? (
          <>
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M8 3v3a2 2 0 0 1-2 2H3m18 0h-3a2 2 0 0 1-2-2V3m0 18v-3a2 2 0 0 1 2-2h3M3 16h3a2 2 0 0 1 2 2v3" />
            </svg>
            Exit Full Screen
          </>
        ) : (
          <>
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M3 8V3h5M3 16v5h5m8-5v5h5M21 8V3h-5" />
            </svg>
            Full Screen
          </>
        )}
      </FullScreenButton>

      {patternType === 'checkerboard' && (
        <Checkerboard 
          color1={getBackgroundColor()} 
          color2="rgba(0,0,0,0.5)" 
          size={checkerboardSize} 
        />
      )}

      {showGridLines && (
        <Grid 
          columns={gridSize} 
          showGridLines={showGridLines}
          transitionSpeed={transitionSpeed}
        >
          {Array(gridSize * gridSize).fill(0).map((_, i) => (
            <Cell key={i} color={getBackgroundColor()} />
          ))}
        </Grid>
      )}

      {showCrosshair && <Crosshair />}

      <ControlPanel isMinimized={isMinimized}>
        <PanelHeader isMinimized={isMinimized}>
          <h2>Uniformity Controls</h2>
          <MinimizeButton onClick={() => setIsMinimized(!isMinimized)}>
            {isMinimized ? (
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M18 15l-6-6-6 6" />
              </svg>
            ) : (
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M6 9l6 6 6-6" />
              </svg>
            )}
          </MinimizeButton>
        </PanelHeader>

        {!isMinimized && (
          <>
            <Description>
              Test your display's color and brightness uniformity across the entire screen. 
              Use different colors and grid patterns to identify any inconsistencies in 
              backlight distribution or color reproduction.
            </Description>

            <Section>
              <h3>Background Color</h3>
              <ColorPreview color={getBackgroundColor()} />
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem', marginBottom: '1rem' }}>
                {['white', 'red', 'green', 'blue', 'black'].map(color => (
                  <ToggleButton
                    key={color}
                    active={selectedColor === color}
                    onClick={() => setSelectedColor(color)}
                  >
                    {color.charAt(0).toUpperCase() + color.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            <Section>
              <h3>Brightness</h3>
              <RangeControl>
                <label>
                  <span>Level: {brightness}%</span>
                </label>
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={brightness}
                  onChange={(e) => setBrightness(Number(e.target.value))}
                />
              </RangeControl>
            </Section>

            <Section>
              <h3>Pattern Type</h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem', marginBottom: '1rem' }}>
                {['solid', 'checkerboard'].map(type => (
                  <ToggleButton
                    key={type}
                    active={patternType === type}
                    onClick={() => setPatternType(type)}
                  >
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            {patternType === 'checkerboard' && (
              <Section>
                <h3>Checkerboard Size</h3>
                <RangeControl>
                  <label>
                    <span>Size: {checkerboardSize}px</span>
                  </label>
                  <input
                    type="range"
                    min="20"
                    max="200"
                    value={checkerboardSize}
                    onChange={(e) => setCheckerboardSize(Number(e.target.value))}
                  />
                </RangeControl>
              </Section>
            )}

            <Section>
              <h3>Grid Pattern</h3>
              <ToggleButton
                active={showGridLines}
                onClick={() => setShowGridLines(!showGridLines)}
                style={{ marginBottom: '1rem' }}
              >
                {showGridLines ? 'Hide Grid' : 'Show Grid'}
              </ToggleButton>

              {showGridLines && (
                <RangeControl>
                  <label>
                    <span>Grid Size: {gridSize}x{gridSize}</span>
                  </label>
                  <input
                    type="range"
                    min="2"
                    max="8"
                    value={gridSize}
                    onChange={(e) => setGridSize(Number(e.target.value))}
                  />
                </RangeControl>
              )}
            </Section>

            <Section>
              <h3>Additional Tools</h3>
              <ToggleButton
                active={showCrosshair}
                onClick={() => setShowCrosshair(!showCrosshair)}
                style={{ marginBottom: '1rem' }}
              >
                {showCrosshair ? 'Hide Crosshair' : 'Show Crosshair'}
              </ToggleButton>

              <RangeControl>
                <label>
                  <span>Transition Speed: {transitionSpeed}ms</span>
                </label>
                <input
                  type="range"
                  min="0"
                  max="1000"
                  value={transitionSpeed}
                  onChange={(e) => setTransitionSpeed(Number(e.target.value))}
                />
              </RangeControl>
            </Section>

            <Section>
              <CollapsibleHeader 
                onClick={() => setIsDisplayInfoOpen(!isDisplayInfoOpen)}
                isOpen={isDisplayInfoOpen}
              >
                <h3>Display Information</h3>
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M19 9l-7 7-7-7" />
                </svg>
              </CollapsibleHeader>
              <CollapsibleContent isOpen={isDisplayInfoOpen}>
                <TechnicalInfo>
                  <div>
                    <span>Resolution:</span>
                    <span>{window.screen.width} × {window.screen.height}</span>
                  </div>
                  <div>
                    <span>Pixel Density:</span>
                    <span>{window.devicePixelRatio.toFixed(2)}x</span>
                  </div>
                  <div>
                    <span>Display Scaling:</span>
                    <span>{Math.round(window.devicePixelRatio * 100)}%</span>
                  </div>
                </TechnicalInfo>
              </CollapsibleContent>
            </Section>

            <Section>
              <ResetButton onClick={handleReset}>
                Reset settings
              </ResetButton>
            </Section>
          </>
        )}
      </ControlPanel>
    </TestContainer>
  );
};

export default UniformityTest;
