import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const TestContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: ${props => props.gradient};
  transition: background 0.3s ease;

  ${props => props.splitView && `
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 2px;
    background: #000;

    &:before {
      content: '';
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 2px;
      height: 100%;
      background: rgba(255, 255, 255, 0.3);
    }
  `}
`;

const GradientPanel = styled.div`
  width: 100%;
  height: 100%;
  background: ${props => props.gradient};
`;

const ExitButton = styled.button`
  position: fixed;
  top: 1.5rem;
  left: 1.5rem;
  background: rgba(0, 0, 0, 0.8);
  color: white;
  border: none;
  border-radius: 0.5rem;
  padding: 0.75rem 1.25rem;
  font-size: 1.1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.2s ease;
  z-index: 100;

  &:hover {
    background: rgba(0, 0, 0, 0.9);
    transform: translateY(-1px);
  }

  &:active {
    transform: translateY(0);
  }

  svg {
    width: 20px;
    height: 20px;
  }
`;

const FullScreenButton = styled(ExitButton)`
  left: auto;
  right: 1.5rem;
`;

const ControlPanel = styled.div`
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  background: white;
  border-radius: 0.75rem;
  box-shadow: 0 5px 30px rgba(0, 0, 0, 0.15);
  width: 400px;
  padding: ${props => props.isMinimized ? '1.25rem' : '2rem'};
  color: #333;
  transition: all 0.3s ease;
  transform: translateY(${props => props.isMinimized ? 'calc(100% - 4rem)' : '0'});
  backdrop-filter: blur(10px);
  max-height: calc(100vh - 4rem);
  overflow-y: auto;
`;

const PanelHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: ${props => props.isMinimized ? '0' : '1.5rem'};
  padding-bottom: ${props => props.isMinimized ? '0' : '1rem'};
  border-bottom: ${props => props.isMinimized ? 'none' : '1px solid rgba(0, 0, 0, 0.1)'};

  h2 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 600;
    color: #333;
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
`;

const MinimizeButton = styled.button`
  background: #4169e1;
  border: none;
  color: white;
  font-size: 1.75rem;
  cursor: pointer;
  width: 2.5rem;
  height: 2.5rem;
  line-height: 1;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-1px);
    box-shadow: 0 2px 8px rgba(65, 105, 225, 0.3);
  }

  &:active {
    transform: translateY(0);
  }

  svg {
    width: 24px;
    height: 24px;
  }
`;

const Description = styled.p`
  margin: 0 0 2rem 0;
  color: #666;
  line-height: 1.5;
  font-size: 0.95rem;
`;

const Section = styled.div`
  margin-bottom: 2rem;

  h3 {
    margin: 0 0 1rem 0;
    font-size: 1rem;
    font-weight: 600;
    color: #333;
  }

  &:last-child {
    margin-bottom: 0;
  }
`;

const RangeControl = styled.div`
  label {
    display: block;
    margin-bottom: 0.5rem;
    color: #666;
    font-size: 0.9rem;
  }

  input[type="range"] {
    width: 100%;
    margin: 0.5rem 0;
  }
`;

const ColorPreview = styled.div`
  width: 100%;
  height: 40px;
  border-radius: 4px;
  margin-bottom: 1rem;
  background: ${props => props.gradient};
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const ToggleButton = styled.button`
  background: ${props => props.active ? '#4169e1' : '#f0f0f0'};
  color: ${props => props.active ? 'white' : '#333'};
  border: none;
  border-radius: 4px;
  padding: 0.75rem;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease;
  width: 100%;

  &:hover {
    background: ${props => props.active ? '#365bb7' : '#e0e0e0'};
  }
`;

const ResetButton = styled.button`
  width: 100%;
  padding: 1rem;
  background: #4169e1;
  color: white;
  border: none;
  border-radius: 0.5rem;
  cursor: pointer;
  font-weight: 500;
  font-size: 1.1rem;
  transition: all 0.2s ease;

  &:hover {
    background: #3658c5;
  }
`;

const ColorGradientTest = () => {
  const [steps, setSteps] = useState(32);
  const [selectedColor, setSelectedColor] = useState('white');
  const [isMinimized, setIsMinimized] = useState(false);
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [gradientDirection, setGradientDirection] = useState('horizontal');
  const [gradientType, setGradientType] = useState('linear');
  const [showInfo, setShowInfo] = useState(false);
  const [splitView, setSplitView] = useState(false);
  const [compareSteps, setCompareSteps] = useState(256);
  const [distribution, setDistribution] = useState('linear');

  const handleReset = () => {
    setSteps(32);
    setSelectedColor('white');
    setGradientDirection('horizontal');
    setGradientType('linear');
    setShowInfo(false);
    setSplitView(false);
    setCompareSteps(256);
    setDistribution('linear');
  };

  const getColor = () => {
    const colors = {
      white: 'rgb(255, 255, 255)',
      red: 'rgb(255, 0, 0)',
      green: 'rgb(0, 255, 0)',
      blue: 'rgb(0, 0, 255)',
      magenta: 'rgb(255, 0, 255)',
      yellow: 'rgb(255, 255, 0)',
      cyan: 'rgb(0, 255, 255)'
    };
    return colors[selectedColor] || colors.white;
  };

  const getGradientDirection = () => {
    switch (gradientDirection) {
      case 'horizontal':
        return 'to right';
      case 'vertical':
        return 'to bottom';
      case 'diagonal-1':
        return '45deg';
      case 'diagonal-2':
        return '135deg';
      default:
        return 'to right';
    }
  };

  const distributeSteps = (i, totalSteps) => {
    if (distribution === 'linear') {
      return i / totalSteps;
    }
    // Emphasize dark regions with more steps
    return Math.pow(i / totalSteps, 2);
  };

  const generateGradient = (stepCount = steps) => {
    const targetColor = getColor();
    const match = targetColor.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
    const [_, r, g, b] = match.map(Number);

    if (gradientType === 'radial') {
      return generateRadialGradient(r, g, b, stepCount);
    }

    // Create distinct color bands
    const gradientStops = [];
    for (let i = 0; i <= stepCount; i++) {
      const ratio = distributeSteps(i, stepCount);
      const startPercent = (i / stepCount) * 100;
      const endPercent = ((i + 1) / stepCount) * 100;

      const currentR = Math.round(r * ratio);
      const currentG = Math.round(g * ratio);
      const currentB = Math.round(b * ratio);
      const color = `rgb(${currentR}, ${currentG}, ${currentB})`;

      if (i < stepCount) {
        gradientStops.push(`${color} ${startPercent}%`);
        gradientStops.push(`${color} ${endPercent}%`);
      }
    }

    const direction = getGradientDirection();
    return `linear-gradient(${direction}, ${gradientStops.join(', ')})`;
  };

  const generateRadialGradient = (r, g, b, stepCount) => {
    const gradientStops = [];
    for (let i = 0; i <= stepCount; i++) {
      const ratio = distributeSteps(i, stepCount);
      const percent = (i / stepCount) * 100;

      const currentR = Math.round(r * ratio);
      const currentG = Math.round(g * ratio);
      const currentB = Math.round(b * ratio);
      const color = `rgb(${currentR}, ${currentG}, ${currentB})`;

      gradientStops.push(`${color} ${percent}%`);
    }

    return `radial-gradient(circle at center, ${gradientStops.join(', ')})`;
  };

  const renderInfoOverlay = () => {
    if (!showInfo) return null;

    const targetColor = getColor();
    const match = targetColor.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
    const [_, r, g, b] = match.map(Number);

    return (
      <div style={{
        position: 'absolute',
        top: '10px',
        left: '10px',
        background: 'rgba(0, 0, 0, 0.8)',
        color: 'white',
        padding: '10px',
        borderRadius: '5px',
        fontSize: '14px'
      }}>
        <div>Target Color: RGB({r}, {g}, {b})</div>
        <div>Steps: {steps}</div>
        <div>Distribution: {distribution}</div>
        <div>Type: {gradientType}</div>
        <div>Direction: {gradientDirection}</div>
      </div>
    );
  };

  useEffect(() => {
    const handleFullScreenChange = () => {
      setIsFullScreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullScreenChange);
    return () => {
      document.removeEventListener('fullscreenchange', handleFullScreenChange);
    };
  }, []);

  const toggleFullScreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen().catch((err) => {
        console.log(`Error attempting to enable fullscreen: ${err.message}`);
      });
      setIsFullScreen(true);
    } else {
      document.exitFullscreen();
      setIsFullScreen(false);
    }
  };

  return (
    <TestContainer gradient={generateGradient()} splitView={splitView}>
      <ExitButton onClick={() => window.history.back()}>
        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="M15 19l-7-7 7-7" />
        </svg>
        Exit Test
      </ExitButton>

      <FullScreenButton onClick={toggleFullScreen}>
        {isFullScreen ? (
          <>
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M8 3v3a2 2 0 0 1-2 2H3m18 0h-3a2 2 0 0 1-2-2V3m0 18v-3a2 2 0 0 1 2-2h3M3 16h3a2 2 0 0 1 2 2v3" />
            </svg>
            Exit Full Screen
          </>
        ) : (
          <>
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M3 8V3h5M3 16v5h5m8-5v5h5M21 8V3h-5" />
            </svg>
            Full Screen
          </>
        )}
      </FullScreenButton>

      <ControlPanel isMinimized={isMinimized}>
        <PanelHeader isMinimized={isMinimized}>
          <h2>Gradient Controls</h2>
          <MinimizeButton onClick={() => setIsMinimized(!isMinimized)}>
            {isMinimized ? (
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M18 15l-6-6-6 6" />
              </svg>
            ) : (
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M6 9l6 6 6-6" />
              </svg>
            )}
          </MinimizeButton>
        </PanelHeader>

        {!isMinimized && (
          <>
            <Description>
              Test your display's gradient rendering capabilities and check for color banding.
              Adjust the target color and number of steps to evaluate smooth color transitions.
            </Description>

            <Section>
              <h3>Target Color</h3>
              <ColorPreview gradient={generateGradient()} />
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem' }}>
                {['white', 'red', 'green', 'blue', 'magenta', 'yellow', 'cyan'].map(color => (
                  <ToggleButton
                    key={color}
                    active={selectedColor === color}
                    onClick={() => setSelectedColor(color)}
                  >
                    {color.charAt(0).toUpperCase() + color.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            <Section>
              <h3>Gradient Steps</h3>
              <RangeControl>
                <label>
                  <span>Steps: {steps}</span>
                </label>
                <input
                  type="range"
                  min="8"
                  max="256"
                  step="8"
                  value={steps}
                  onChange={(e) => setSteps(Number(e.target.value))}
                />
              </RangeControl>
            </Section>

            <Section>
              <h3>Gradient Direction</h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem' }}>
                {['horizontal', 'vertical', 'diagonal-1', 'diagonal-2'].map(direction => (
                  <ToggleButton
                    key={direction}
                    active={gradientDirection === direction}
                    onClick={() => setGradientDirection(direction)}
                  >
                    {direction.charAt(0).toUpperCase() + direction.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            <Section>
              <h3>Gradient Type</h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem' }}>
                {['linear', 'radial'].map(type => (
                  <ToggleButton
                    key={type}
                    active={gradientType === type}
                    onClick={() => setGradientType(type)}
                  >
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            <Section>
              <h3>Step Distribution</h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '0.5rem' }}>
                {['linear', 'non-linear'].map(dist => (
                  <ToggleButton
                    key={dist}
                    active={distribution === dist}
                    onClick={() => setDistribution(dist)}
                  >
                    {dist.charAt(0).toUpperCase() + dist.slice(1)}
                  </ToggleButton>
                ))}
              </div>
            </Section>

            <Section>
              <h3>Info Overlay</h3>
              <ToggleButton
                active={showInfo}
                onClick={() => setShowInfo(!showInfo)}
              >
                {showInfo ? 'Hide Info' : 'Show Info'}
              </ToggleButton>
            </Section>

            <Section>
              <h3>Split View</h3>
              <ToggleButton
                active={splitView}
                onClick={() => setSplitView(!splitView)}
              >
                {splitView ? 'Disable Split View' : 'Enable Split View'}
              </ToggleButton>
            </Section>

            <Section>
              <h3>Compare Steps</h3>
              <RangeControl>
                <label>
                  <span>Steps: {compareSteps}</span>
                </label>
                <input
                  type="range"
                  min="8"
                  max="256"
                  step="8"
                  value={compareSteps}
                  onChange={(e) => setCompareSteps(Number(e.target.value))}
                />
              </RangeControl>
            </Section>

            <Section>
              <ResetButton onClick={handleReset}>Reset Settings</ResetButton>
            </Section>
          </>
        )}
      </ControlPanel>

      {splitView ? (
        <>
          <GradientPanel gradient={generateGradient(steps)} />
          <GradientPanel gradient={generateGradient(compareSteps)} />
        </>
      ) : (
        <div style={{ flex: 1, background: generateGradient() }} />
      )}

      {renderInfoOverlay()}
    </TestContainer>
  );
};

export default ColorGradientTest;
